document.querySelectorAll('nav .nav-link[href="#contact"]').forEach((function(t){t.addEventListener("click",(function(t){var e=document.querySelector(".appointment-info");if(e){t.preventDefault();var n=e.getBoundingClientRect().top+window.pageYOffset-20;window.scrollTo({top:n,behavior:"smooth"})}}))})),document.querySelectorAll(".service-link").forEach((function(t){t.addEventListener("click",(function(e){var n=t.getAttribute("href");if(n&&n.startsWith("/#")){var o=n.split("#")[1],i=document.getElementById(o);if(i){e.preventDefault(),window.history.pushState(null,"",n);var r=i.getBoundingClientRect().top+window.pageYOffset;window.scrollTo({top:r,behavior:"smooth"})}}}))}));